    //Method to verify fields
    public boolean verify()
    {

        if (jTextField_FName.getText().equals("") || jTextField_LName.getText().equals("")
                || jTextField_IDProof.getText().equals("") || jTextArea_Address.getText().equals(""))
        {
            JOptionPane.showMessageDialog(null, "One or More Empty Field(s).");
            return false;
        } else
        {
            return true;
        }

    }
    //Method to add entry
    private void jButton_AddActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_jButton_AddActionPerformed
    {//GEN-HEADEREND:event_jButton_AddActionPerformed
        //Get text of fields
        String fname = jTextField_FName.getText();
        String lname = jTextField_LName.getText();
        String idproof = jTextField_IDProof.getText();
        String address = jTextArea_Address.getText();

        //Verify fields
        if (verify())
        {
            //Insert data
            r.insertUpdateDeleteRenter('i', null, fname, lname, idproof, address);
            //Set text of fields after add
            jTextField_FName.setText("");
            jTextField_LName.setText("");
            jTextField_IDProof.setText("");
            jTextArea_Address.setText("");
        }
    }//GEN-LAST:event_jButton_AddActionPerformed

