    //Method to validate and limit field
    private void jTextField_FNameKeyTyped(java.awt.event.KeyEvent evt)//GEN-FIRST:event_jTextField_FNameKeyTyped
    {//GEN-HEADEREND:event_jTextField_FNameKeyTyped
        if (Character.isDigit(evt.getKeyChar()) || jTextField_FName.getText().length() > 48)
        {
            evt.consume();
        }
    }//GEN-LAST:event_jTextField_FNameKeyTyped

